mapper.select:
SELECT houseno, nomination, area, terrain, price, hdate, likes, loca, hname, adminid
FROM house
WHERE houseno = #{houseno}

mapper.update:
UPDATE house
SET hname=#{hname}, price=#{price}
WHERE houseno = #{houseno}

