mapper.select:
SELECT COUNT(id) as cnt
FROM member
WHERE id=#{id}

SELECT COUNT(*) 
FROM member
WHERE email = #{email}

SELECT memberid, id, passwd, name, phone, email, address, grade
FROM member
WHERE memberid = #{memberid}

SELECT memberid, id, passwd, name, phone, email, address, grade
FROM member
WHERE id = #{id}

mapper.update:
UPDATE member 
SET name=#{name}, phone=#{phone}, address=#{address}
WHERE memberid=#{memberid}

mapper.select:
SELECT COUNT(memberid) as cnt
FROM member
WHERE memberid=#{memberid} AND passwd=#{passwd}

mapper.update:
UPDATE member SET
passwd=#{passwd}
WHERE memberid=#{memberid}

mapper.select:
SELECT COUNT(memberid) as cnt
FROM member
WHERE id=#{id} AND passwd=#{passwd}
